                    FROM madlibtestdata.{table_name} --table_name
		    ORDER BY {id}
                    LIMIT {count} --values count
                    )ht;

                SELECT ({madlib_schema}.wsr_test({value} ORDER BY abs({value}), {precision})).* FROM (
                    SELECT *
                    FROM madlibtestdata.{table_name} --table_name
		    ORDER BY {id}
                    LIMIT {count} --values count
                    )ht;

SELECT ({madlib_schema}.wsr_test({value} ORDER BY abs({value}), {precision} )).* FROM (
